define('common/services/dialog/dialog',[
  'common/lodash'
], function (_) {
  'use strict';

  return ['$uibModal', '$rootElement', '$templateCache',
    function ($modal, $rootElement, $templateCache) {

      return {

        /**
         * Opens the small dialog modal
         *
         * @param {object} options
         *   Contains the labels for OK and Cancel button, the body
         *   of the modal, and the title of the modal
         * @return {Promise}
         */
        open: function (options) {
          var $children;

          if (options && typeof options !== 'object') {
            return;
          }

          $children = $rootElement.children();

          return $modal.open({
            appendTo: $children.length ? $children.eq(0) : $rootElement,
            size: 'sm',
            controller: 'DialogCtrl',
            template: $templateCache.get('dialog.html'),
            resolve: {
              content: function (){
                return {
                  copyCancel: options.copyCancel || '',
                  copyConfirm: options.copyConfirm || '',
                  classConfirm: options.classConfirm || '',
                  title: options.title || '',
                  msg: options.msg || ''
                };
              },
              onConfirm: function () {
                return _.isFunction(options.onConfirm) ? options.onConfirm : null;
              }
            }
          }).result;
        }
      };
    }
  ];
});

define('common/controllers/dialog/dialog-ctrl',[], function () {
  'use strict';

  return ['$q', '$scope','$uibModalInstance', 'content', 'onConfirm',
    function ($q, $scope, $modalInstance, content, onConfirm) {
      $scope.loading = false

      $scope.title = content.title || 'CiviHR';
      $scope.msg = content.msg || '';
      $scope.copyConfirm = content.copyConfirm || 'Yes';
      $scope.copyCancel = content.copyCancel || 'Cancel';
      $scope.classConfirm = content.classConfirm || 'btn-primary';

      $scope.confirm = function () {
        $scope.loading = true;

        $q
          .resolve(onConfirm ? onConfirm() : null)
          .then(function () {
            $modalInstance.close(true);
          });
      };

      $scope.cancel = function () {
        $modalInstance.close(false);
      };
    }
  ];
});

